import React, { useState, useEffect } from 'react';
import axios from 'axios';

const OwnerComponent = () => {
  const [owners, setOwners] = useState([]);
  const [newOwner, setNewOwner] = useState({
    username: '',
    owner: '',
    ownerNumber: '',
    buzSocials: '',
    location: '',
    website: '',
    city: '',
    niche: ''
  });
  const [editOwner, setEditOwner] = useState(null);

  useEffect(() => {
    fetchOwners();
  }, []);

  const fetchOwners = async () => {
    try {
      const response = await axios.get('http://localhost:3000/Owner');
      setOwners(response.data);
    } catch (error) {
      console.error('Error fetching owners:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewOwner((prevOwner) => ({
      ...prevOwner,
      [name]: value
    }));
  };

  const addOwner = async () => {
    try {
      const response = await axios.post('http://localhost:3000/Owner', newOwner);
      setOwners((prevOwners) => [...prevOwners, response.data.NewOwner]);
      setNewOwner({
        username: '',
        owner: '',
        ownerNumber: '',
        buzSocials: '',
        location: '',
        website: '',
        city: '',
        niche: ''
      });
    } catch (error) {
      console.error('Error adding owner:', error);
    }
  };

  const deleteOwner = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/Owner/${id}`);
      setOwners((prevOwners) => prevOwners.filter((owner) => owner._id !== id));
    } catch (error) {
      console.error('Error deleting owner:', error);
    }
  };

  const setEditMode = (owner) => {
    setEditOwner(owner);
  };

  const cancelEditMode = () => {
    setEditOwner(null);
  };

  const updateOwner = async () => {
    try {
      const response = await axios.put(`http://localhost:3000/Owner/${editOwner._id}`, editOwner);
      setOwners((prevOwners) =>
        prevOwners.map((owner) => (owner._id === response.data._id ? response.data : owner))
      );
      setEditOwner(null);
    } catch (error) {
      console.error('Error updating owner:', error);
    }
  };

  return (
    <div className="container">
      <h1>Owners</h1>
      <table className="table">
        <thead>
          <tr>
            <th>Username</th>
            <th>Owner</th>
            <th>Owner Number</th>
            <th>Buz Socials</th>
            <th>Location</th>
            <th>Website</th>
            <th>City</th>
            <th>Niche</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {owners.map((owner) => (
            <tr key={owner._id}>
              <td>{owner.username}</td>
              <td>{owner.owner}</td>
              <td>{owner.ownerNumber}</td>
              <td>{owner.buzSocials}</td>
              <td>{owner.location}</td>
              <td>{owner.website}</td>
              <td>{owner.city}</td>
              <td>{owner.niche}</td>
              <td>
                <button className="btn btn-primary" onClick={() => setEditMode(owner)}>
                  Edit
                </button>
                <button className="btn btn-danger" onClick={() => deleteOwner(owner._id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Add Owner</h2>
      <div className="row">
        <div className="col-md-6">
          <div className="form-group">
            <label>Username</label>
            <input
              type="text"
              className="form-control"
              name="username"
              value={newOwner.username}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Owner</label>
            <input
              type="text"
              className="form-control"
              name="owner"
              value={newOwner.owner}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Owner Number</label>
            <input
              type="text"
              className="form-control"
              name="ownerNumber"
              value={newOwner.ownerNumber}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Buz Socials</label>
            <input
              type="text"
              className="form-control"
              name="buzSocials"
              value={newOwner.buzSocials}
              onChange={handleInputChange}
            />
          </div>
        </div>
        <div className="col-md-6">
          <div className="form-group">
            <label>Location</label>
            <input
              type="text"
              className="form-control"
              name="location"
              value={newOwner.location}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Website</label>
            <input
              type="text"
              className="form-control"
              name="website"
              value={newOwner.website}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>City</label>
            <input
              type="text"
              className="form-control"
              name="city"
              value={newOwner.city}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Niche</label>
            <input
              type="text"
              className="form-control"
              name="niche"
              value={newOwner.niche}
              onChange={handleInputChange}
            />
          </div>
        </div>
      </div>
      <button className="btn btn-success" onClick={addOwner}>
        Add Owner
      </button>

      {editOwner && (
        <>
          <h2>Edit Owner</h2>
          <div className="row">
            <div className="col-md-6">
              <div className="form-group">
                <label>Username</label>
                <input
                  type="text"
                  className="form-control"
                  name="username"
                  value={editOwner.username}
                  onChange={(e) => setEditOwner({ ...editOwner, username: e.target.value })}
                />
              </div>
              <div className="form-group">
                <label>Owner</label>
                <input
                  type="text"
                  className="form-control"
                  name="owner"
                  value={editOwner.owner}
                  onChange={(e) => setEditOwner({ ...editOwner, owner: e.target.value })}
                />
              </div>
              <div className="form-group">
                <label>Owner Number</label>
                <input
                  type="text"
                  className="form-control"
                  name="ownerNumber"
                  value={editOwner.ownerNumber}
                  onChange={(e) => setEditOwner({ ...editOwner, ownerNumber: e.target.value })}
                />
              </div>
              <div className="form-group">
                <label>Buz Socials</label>
                <input
                  type="text"
                  className="form-control"
                  name="buzSocials"
                  value={editOwner.buzSocials}
                  onChange={(e) => setEditOwner({ ...editOwner, buzSocials: e.target.value })}
                />
              </div>
            </div>
            <div className="col-md-6">
              <div className="form-group">
                <label>Location</label>
                <input
                  type="text"
                  className="form-control"
                  name="location"
                  value={editOwner.location}
                  onChange={(e) => setEditOwner({ ...editOwner, location: e.target.value })}
                />
              </div>
              <div className="form-group">
                <label>Website</label>
                <input
                  type="text"
                  className="form-control"
                  name="website"
                  value={editOwner.website}
                  onChange={(e) => setEditOwner({ ...editOwner, website: e.target.value })}
                />
              </div>
              <div className="form-group">
                <label>City</label>
                <input
                  type="text"
                  className="form-control"
                  name="city"
                  value={editOwner.city}
                  onChange={(e) => setEditOwner({ ...editOwner, city: e.target.value })}
                />
              </div>
              <div className="form-group">
                <label>Niche</label>
                <input
                  type="text"
                  className="form-control"
                  name="niche"
                  value={editOwner.niche}
                  onChange={(e) => setEditOwner({ ...editOwner, niche: e.target.value })}
                />
              </div>
            </div>
          </div>
          <br/>
          <button style={{padding}} className="btn btn-primary" onClick={updateOwner}>
            Update Owner
          </button>
         

          <button className="btn btn-secondary" onClick={cancelEditMode}>
            Cancel
          </button>
        </>
      )}
    </div>
  );
};

export default OwnerComponent;
